import React, {useState} from 'react';
// Import Styled Components
import styled from 'styled-components';
import {StyledAbout} from '../styles';

const FaqSection = () => {
    const [faqToggle, setFaqToggle] = useState(false);

    return(
        <StyledFaq>
            <h2>Any Questions <span>FAQ</span></h2>
            <div onClick={() => setFaqToggle(!faqToggle)} className="question">
                <h4>How do I Start?</h4>
                <div className="answer">
                    <p>Lorem ipsum dolor sit amet.</p>
                    {faqToggle && ( 
                    <p>             {/*If faqToggle is true and the p tag is true, display the p tag*/}
                     Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                     sollicitudin suscipit eget.
                    </p>
                    )}
                </div>
                <div className="faq-line"></div>
            </div>
            <div className="question">
                <h4>Daily Schedule</h4>
                <div className="answer">
                    <p>Lorem ipsum dolor sit amet.</p>
                    <p>
                     Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                     sollicitudin suscipit eget.
                    </p>
                </div>
                <div className="faq-line"></div>
            </div>
            <div className="question">
                <h4>Different Payment Methods</h4>
                <div className="answer">
                    <p>Lorem ipsum dolor sit amet.</p>
                    <p>
                     Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                     sollicitudin suscipit eget.
                    </p>
                </div>
                <div className="faq-line"></div>
            </div>
            <div className="question">
                <h4>What Products Do you Offer?</h4>
                <div className="answer">
                    <p>Lorem ipsum dolor sit amet.</p>
                    <p>
                     Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                     sollicitudin suscipit eget.
                    </p>
                </div>
            </div>
        </StyledFaq>
    );
};

const StyledFaq = styled(StyledAbout)`
    display: block;
    span{
        display: block;
    }
    h2{
        padding-bottom: 2rem;
        font-weight: lighter;
    }
    .faq-line{
        background: #cccccc;
        height: 0.2rem;
        margin: 2rem 0rem;
        width: 100%;
    }
    .question{
        padding: 3rem 0rem;
        cursor: pointer;
    }
    .answer{
        padding: 2rem 0rem;
        p{
            padding: 1rem 0rem;
        }
    }
`;

export default FaqSection;
