import React from 'react';
// Import Framer Motion
import {motion} from 'framer-motion';
// Import Images
import home1 from '../img/home1.png';
// Import Styled Components
//import styled from 'styled-components';
// Import Styles
import {
    StyledAbout,
    StyledDescription,
    StyledHide,
    StyledImage,
} from '../styles';

const AboutSection = () => {

    const titleAnim = {
        hidden: {opacity: 0},
        show: {opacity: 1, transition: {duration: 2} },
    };

    const container = {
        hidden: {x: 100},
        show: {x: 0, transition: {duration: 1} },
    };

    return(
        <StyledAbout>
            <StyledDescription>
                <motion.div className="title" variants={container} initial="hidden" animate="show">
                    <StyledHide>
                        <motion.h2 
                            variants={titleAnim} initial="hidden" animate="show"
                            >
                                We work to make
                        </motion.h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>your <span>dreams</span> come</h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>true.</h2>
                    </StyledHide>
                </motion.div>
                <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                sollicitudin suscipit eget finibus erat. Phasellus eu elit at libero facilisis iaculis. 
                </p>
                <button>Contact me</button>
            </StyledDescription>
            <StyledImage>
                <img src={home1} alt="home img"/>
            </StyledImage>
        </StyledAbout>
    );
};

export default AboutSection;
