import home1 from '../img/home1.png';
// Import Styled Components
//import styled from 'styled-components';
// Import Styles
import {
    StyledAbout,
    StyledDescription,
    StyledHide,
    StyledImage,
} from '../styles';

const AboutSection = () => {
    return(
        <StyledAbout>
            <StyledDescription>
                <div className="title">
                    <StyledHide>
                        <motion.h2 
                            animate={{ opacity: 1, transition: { duration: 2} }} 
                            initial={{ opacity: 0}}>
                                We work to make
                        </motion.h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>your <span>dreams</span> come</h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>true.</h2>
                    </StyledHide>
                </div>
                <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                sollicitudin suscipit eget finibus erat. Phasellus eu elit at libero facilisis iaculis. 
                </p>
                <button>Contact me</button>
            </StyledDescription>
            <StyledImage>
                <img src={home1} alt="home img"/>
            </StyledImage>
        </StyledAbout>
    );
};

export default AboutSection;
