import React from 'react';
//import home1 from '../img/home1.png';
//import cat from '../img/cute_cat.jpg';
import night from '../img/night.jpg';
//Styled
import styled from 'styled-components';

const AboutSection = () => {
    return(
        <StyledAbout>
            <StyledDescription>
                <div className="title">
                    <StyledHide>
                        <h2>We are here to</h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>make your <span>dreams</span></h2>
                    </StyledHide>
                    <StyledHide>
                        <h2>come true</h2>
                    </StyledHide>
                </div>
                <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Ut sed 
                diam pharetra, vehicula purus sed, sagittis odio. Cras tellus tortor,
                </p>
                <button>Contact Me</button>
            </StyledDescription>
            <StyledImage>
                <img src={night} alt="home-image"/>
            </StyledImage>
        </StyledAbout>
    );
};

//Styled Components 
const StyledAbout = styled.div`
    min-height: 90vh;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 5rem 10rem;
    color: black;
`;

const StyledDescription = styled.div`
    flex: 1;
    padding-right: 5rem;
    h2{
        font-weight: lighter;
    }
`;

const StyledImage = styled.div`
    flex:1;
    overflow: hidden;
    img{
        width: 100%;
        height: 90vh;
        object-fit: cover;
    }
`;

const StyledHide = styled.div`
    overflow: hidden
`;

export default AboutSection
