import React from 'react';
// Import Framer Motion
import {motion} from 'framer-motion';
import {titleAnimation, fade, photoAnimation} from '../animation';
// Import Images
import home1 from '../img/home1.png';
// Import Styled Components
import styled from 'styled-components';
// Import Styles
import {
    StyledAbout,
    StyledDescription,
    StyledHide,
    StyledImage,
} from '../styles';

const AboutSection = () => {

    const titleAnim = {
        hidden: {opacity: 0},
        show: {opacity: 1, transition: {duration: 2} },
    };

    const container = {
        hidden: {x: 100},
        show: {x: 0, transition: {duration: 0.75, ease: "easeIn", staggerChildren: 1, when: "afterChildren",} },
    };

    return(
        <StyledAbout>
            <StyledDescription>
                <motion.div>
                    <StyledHide>
                        <motion.h2 variants={titleAnimation}> 
                            We work to make
                        </motion.h2>
                    </StyledHide>
                    <StyledHide>
                        <motion.h2 variants={titleAnimation}><span>dreams</span> come</motion.h2>
                    </StyledHide>
                    <StyledHide>
                        <motion.h2 variants={titleAnimation}>true.</motion.h2>
                    </StyledHide>
                </motion.div>
                <motion.p variants={fade}>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus sit amet diam ut dui 
                sollicitudin suscipit eget finibus erat. Phasellus eu elit at libero facilisis iaculis. 
                </motion.p>
                <motion.button variants={fade}>Contact me</motion.button>
            </StyledDescription>
            <StyledImage>
                <motion.img variants={photoAnimation} src={home1} alt="home img"/>
            </StyledImage>
        </StyledAbout>
    );
};

export default AboutSection;
