import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {motion} from 'framer-motion';
import {useLocation} from 'react-router-dom';

const Nav = () => {
    const {pathname} = useLocation();
    return(
        <StyledNav>
            <h1><Link to="/" id="logo">Shenal</Link></h1>
            <ul>
                <li>
                    <Link to="/">1. About Me</Link>
                    <Line 
                        transition={{duration: 0.75}} 
                        initial={{width: "0%"}} 
                        animate={{width: pathname === '/' ? "46%" : "0%"}}
                    />
                </li>
                <li>
                    <Link to="/work">2. My Work</Link>
                    <Line 
                        transition={{duration: 0.75}} 
                        initial={{width: "0%"}} 
                        animate={{width: pathname === '/work' ? "45%" : "0%"}}
                    />
                </li>
                <li>
                    <Link to="/contact">3. Contact Me</Link>
                    <Line 
                        transition={{duration: 0.75}} 
                        initial={{width: "0%"}} 
                        animate={{width: pathname === '/contact' ? "46%" : "0%"}}
                    />
                </li>
            </ul>
        </StyledNav>
    );
};

const StyledNav = styled.nav`
    min-height: 10vh;
    display: flex;
    margin: auto;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 10rem;
    background-color: #302f30;
    position: sticky;
    top: 0;
    z-index: 10;
    a{
        color: white;
        text-decoration: none;
    }
    ul{
        display: flex;
        list-style: none;
    }
    #logo{
        font-size: 1.5rem;
        font-family: "Lobster", cursive;
        font-weight: lighter;
    }
    li{
        padding-left: 7rem;
        position: relative;
    }
    @media (max-width: 1150px){
        flex-direction: column;
        padding: 2rem 1rem;
        #logo{
            display: inline-block;
            margin: 2rem;
        }
        ul{
            padding: 2rem;
            justify-content: space-around;
            width: 100%;
            li{
                padding: 0;
            }
        }
    }
`;

const Line = styled(motion.div)`
    height: 0.3rem;
    background: #23d997;
    width: 0%;
    position: absolute;
    bottom: -80%;
    left: 55%;
    @media (max-width: 1150px){
        left: 0%;
    }
`;

export default Nav;
